def crypt(text,alph,key):
	r = ""
	for c in text.lower():
		if c in alph:
			c = key[alph.index(c)]
		r = r + c
	return r

def frec(text,alph):
	r = [(text.lower().count(c),c) for c in alph]
	r.sort()
	return "".join([x[1] for x in r])

